// src/components/Dropdown.js

import React, { useState } from 'react';
import { FaBars } from 'react-icons/fa';

function Dropdown() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="relative">
      <button
        onClick={toggleDropdown}
        className="text-2xl cursor-pointer focus:outline-none"
      >
        <FaBars />
      </button>
      {isOpen && (
        <div className="absolute right-0 mt-2 bg-white border border-gray-300 rounded-lg shadow-md">
          <ul>
            <li>
              <a href="#name-occupation" className="block px-4 py-2" onClick={() => scrollToSection('name-occupation')}>Name and Occupation</a>
            </li>
            <li>
              <a href="#about" className="block px-4 py-2" onClick={() => scrollToSection('about')}>About</a>
            </li>
            <li>
              <a href="#portfolio" className="block px-4 py-2" onClick={() => scrollToSection('portfolio')}>Portfolio</a>
            </li>
            <li>
              <a href="#contact" className="block px-4 py-2" onClick={() => scrollToSection('contact')}>Contact Me</a>
            </li>
          </ul>
        </div>
      )}
    </div>
  );
}

export default Dropdown;
