import React from 'react';
import Dropdown from './Dropdown'; // Import your Dropdown component

function StickyNavbar() {
    const [isVisible, setIsVisible] = useState(false);
  
    useEffect(() => {
      // Add scroll event listener to show/hide the navbar
      const handleScroll = () => {
        if (window.scrollY > 100) {
          setIsVisible(true);
        } else {
          setIsVisible(false);
        }
      };
  
      window.addEventListener('scroll', handleScroll);
  
      // Remove the event listener when the component unmounts
      return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }, []);
  
    return (

   
    <nav className={`fixed top-0 left-0 w-full p-4 transition-all duration-300 ${isVisible ? 'bg-gray-200' : 'bg-transparent'} z-50`}>
        
        <Dropdown />
    </nav>
   
  );
}

export default StickyNavbar;
